from ext import db, app, login_manager, datetime
from flask_login import UserMixin

class BaseModel:
    def create(self):
        db.session.add(self)
        db.session.commit()

    def delete(self):
        db.session.delete(self)
        db.session.commit()

    def save(self):
        db.session.commit()
    

class Writeri(db.Model, BaseModel):
    id = db.Column(db.Integer, primary_key=True)
    category_id=db.Column(db.Integer, db.ForeignKey("writer_category.id"))
    name = db.Column(db.String)
    img = db.Column(db.String)
    description = db.Column(db.String)

    category=db.relationship("WriterCategory", back_populates="writers")

class WriterCategory(db.Model, BaseModel):
    id = db.Column(db.Integer, primary_key=True)
    name = db.Column(db.String)

    writers=db.relationship("Writeri")

class User(db.Model, BaseModel, UserMixin):
    id = db.Column(db.Integer, primary_key=True)
    username = db.Column(db.String)
    password = db.Column(db.String)
    role = db.Column(db.String)

    comments = db.relationship("Comments", backref = "commenter")

class Comments(db.Model, BaseModel):
    id = db.Column(db.Integer, primary_key=True)
    content = db.Column(db.String)
    # author = db.Column(db.String)
    date_commented = db.Column(db.DateTime, default = datetime.utcnow)
    commented_id = db.Column(db.Integer, db.ForeignKey("user.id"))

@login_manager.user_loader
def load_user(user_id):
    return(User.query.get(user_id))


